import React, { useState,useEffect, useRef } from 'react'
import useGlobal from '../hooks/useGlobal';
import ProgressBar from './ProgressBar';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleStop, faCirclePlay } from '@fortawesome/free-solid-svg-icons';

const Player = ({guessNum}) => {
  const [timeoutID, setTimeoutID] = useState()
  const [timeStarted, setTimeStarted] = useState(new Date())
  const [duration, setDuration] = useState(16)

  const {CORRECT_SONG, isPlaying,setIsPlaying,playerRef, setHasPlayedToday} = useGlobal();

  //handles song having been played for allocated time


  const stopSong = () => {

    clearTimeout(timeoutID);
    setIsPlaying(false);
    playerRef.current.internalPlayer.pauseVideo();
    playerRef.current.internalPlayer.seekTo(CORRECT_SONG.offset);
    console.log(`Cleared: ${timeoutID}`)
  }

  const playSong = () =>{
    playerRef.current.internalPlayer.playVideo();
    let playFor = guessNum !== undefined? getGuessTime(guessNum):duration*1000
    setTimeoutID(setTimeout(stopSong,playFor)) //guess time secs -> ms
    console.log("set timeout")
    setTimeStarted(new Date())
    setIsPlaying(true)
  }
 //guess of "X" corresponds to failure to guess
 const updateStats = (g)=>{
  const stats = JSON.parse(localStorage.getItem("scores"))
  stats[g] +=1
  localStorage.setItem("scores",JSON.stringify(stats));
}


  useEffect(() => {
    console.log(timeoutID)
  }, [timeoutID])

  const getGuessTime = (gn) =>{ //returns guess-time for corresponding guess num in ms
    return (1 + gn*(gn+1)/2)*1000 
  }

  const onPlayClicked = () =>{
    if(!isPlaying){ // play
      playSong();
      return
    }
    //stop song
    stopSong();

  }

  useEffect(()=>{
    console.log("mount")
    playerRef.current.internalPlayer.setVolume(100);
    playerRef.current.internalPlayer.seekTo(CORRECT_SONG.offset);

    return () => {
      playerRef.current.internalPlayer.seekTo(CORRECT_SONG.offset)
    }
  },[])

  useEffect(()=>{
    if(guessNum !== undefined) return;
    const getDuration = (async() =>{
        const durationPromise = playerRef.current.internalPlayer.getDuration()
        durationPromise.then((d)=>{
          setDuration(Math.floor(d - CORRECT_SONG.offset))
        }
    )})();

  },[playerRef])



  //handles when the user skips while the song is playing (so keeps playing until new time slot)
  useEffect(()=>{
    if(guessNum == 0 || guessNum == undefined || !isPlaying) return //when guessNum is first set
    clearTimeout(timeoutID);
    console.log(`Cleared: ${timeoutID}`)

    if(guessNum > 5) return
    let timeRemaining = getGuessTime(guessNum) - ((new Date()) - timeStarted);
    setTimeoutID(setTimeout(stopSong,timeRemaining))
    console.log("set timeout")
  }, [guessNum])

  useEffect(()=>{
    if(guessNum > 5) 
    {
      clearTimeout(timeoutID);


          console.log(`Cleared: ${timeoutID}`)

      setIsPlaying(true) //so autoplays
      setHasPlayedToday(true)
      updateStats("X")
    }
  },[guessNum])


  return (
    <>
      <ProgressBar isPlaying={isPlaying} guessNum={guessNum} duration={duration}/>
      <button className="play-btn">
        <FontAwesomeIcon icon = {isPlaying? faCircleStop : faCirclePlay} id="play-icon" onClick={onPlayClicked}/>
      </button>
    </>
    )
}

export default Player
