import { createContext,useEffect,useState } from "react";

const GlobalContext = createContext({});

export const GlobalProvider = ({children}) =>{
    const CORRECT_SONG = 
    {
        name: "Mask Off",
        artist:"Future",
        id: "xvZqHgFz51I",
        offset: 84,
    }
    const [hasPlayedToday, setHasPlayedToday] = useState(false);
    const [isCorrect, setIsCorrect] = useState(false);

    useEffect(() =>{
        const lastStarted = new Date(localStorage.getItem("last-started"))
        const lastPlayed = new Date(localStorage.getItem("last-played"))

        if(isToday(lastPlayed)){
            setHasPlayedToday(true);
            resetStoredGuesses();
        }


    },[])

    const resetStoredGuesses = () =>{
        localStorage.setItem("guesses", JSON.stringify(['','','','','','']))
    }

    const isToday = (someDate) => {
        const today = new Date()
        return someDate?.getDate() == today.getDate() &&
          someDate?.getMonth() == today.getMonth() &&
          someDate?.getFullYear() == today.getFullYear()
      }

    return(
        <GlobalContext.Provider value={{CORRECT_SONG,hasPlayedToday,setHasPlayedToday,isCorrect,setIsCorrect}}>
            {children}
        </GlobalContext.Provider>
    )
}

export default GlobalContext;
