  const banner = useRef()

  const atOptions = {
		'key' : '6ee8aa84fa200e34752a5c56a5f8e921',
		'format' : 'iframe',
		'height' : 600,
		'width' : 160,
		'params' : {}
	};



  return (
    <div id="amazon-ad-popup">

        <FontAwesomeIcon icon={faX} id="close" className='text-accent' onClick={() =>setShowAdPopup(false)}/>

        <div className='ad-container' >

        </div>
    </div>
  )
}



export default AdserraAdPopup;
