import React, { useEffect, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome' 
import { faX } from '@fortawesome/free-solid-svg-icons'


const StatsPop = ({close}) => {
  const [xVals, setXVals] = useState([])
  const [yVals,setYVals] = useState([])
  const [maxNumWins, setMaxNumWins] = useState(0)

  useEffect(()=>{
    const stats = JSON.parse(localStorage.getItem("scores"));
    console.log(stats);
    setXVals(Object.keys(stats));
    setYVals(Object.values(stats));
    setMaxNumWins(getMaxValue(Object.values(stats)))
  },[])

  const getMaxValue = (arr) => {
    let max = 0;
    arr.forEach((e)=>{
      max=Math.max(max,e)
    })
    return max;
  }

  return (
    <div className='popup'>
        <FontAwesomeIcon id="close" icon={faX} onClick={close}/> 
        <h1>Stats</h1>
        <div id="barchart">
          <div id= "barchart-bars-container">
            {console.log(yVals.length)}
            {yVals.map((y,i)=> <div className='barchart-bar' style={{height: `${100*maxNumWins/y}%`}} key={i}>{y? y:""}</div>)}
          </div>
          <div id="barchart-labels-container">
            {xVals.map((x,i)=> <p className='barchart-label' key={i}>{x}</p>)}
          </div>
        </div>
    </div>
  )
}

export default StatsPop
