    //setHasPlayedToday(isToday(lastPlayedDate))
  },[])

  return (
    <main className='App'>
        <Header />
        {hasPlayedToday 
        ? <SongPage/>
        : <GuessSong /> }
        <Outlet />
    </main>
  )
}

export default Layout
