import React, { useEffect, useRef, useState } from 'react'
import useGlobal from '../hooks/useGlobal'
import Player from './Player';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCirclePlay, faShareNodes, faCircleStop } from '@fortawesome/free-solid-svg-icons';
import ShareButton from './ShareButton';

const CORRECT_H1 = "Well Done!"
const CORRECT_H2 = "You guessed today's 2010s Heardle. Come back tomorrow to keep the current streak!"
const INCORRECT_H1 = "Better luck next time!"
const INCORRECT_H2 = "You didn't get today's 2010s Heardle, but try again tomorrow!"
const SHARE_LINK = "[Link]";
const SongPage = () => {
    const {CORRECT_SONG, isCorrect} = useGlobal();
    const [isPlaying, setIsPlaying] = useState(true);

    const imgURL = `https://img.youtube.com/vi/${CORRECT_SONG.id}/maxresdefault.jpg`

    const openInNewTab = url => {
        window.open(url, '_blank', 'noopener,noreferrer');
    };

    const copyToClipboard = (text) =>{
        navigator.clipboard.writeText(text)
    }


    //on mount
    useEffect(() => {
        localStorage.setItem("last-played", new Date());
    },[])

    return (
        <div id="songpage-container">
            <p>{console.log(isCorrect)}</p>
            <div id="song-box" onClick={() => openInNewTab(`https://youtu.be/${CORRECT_SONG.id}&t=${Math.floor(CORRECT_SONG.offset)}`)}>
                <img src={imgURL} id="thumbnail-img"/>
                <div id="name-and-artist">
                    <p>{CORRECT_SONG.name}</p>
                    <p id='song-artist'>{CORRECT_SONG.artist}</p>
                </div>
                <img src={"https://cdn-icons-png.flaticon.com/512/1384/1384060.png"} id="yt-icon"></img>
            </div>
            <h1>{isCorrect? CORRECT_H1:INCORRECT_H1} </h1>
            <div id="dashes-container">
                {[...Array(6)].map((x, i) => <div class="dash" key={i}/>)}
            </div>
            <h2>{isCorrect? CORRECT_H2:INCORRECT_H2}</h2>
            <ShareButton/>
            <Player isPlaying={isPlaying} setIsPlaying={setIsPlaying}/>            

            <button className="play-btn">
                <FontAwesomeIcon icon = {isPlaying? faCircleStop : faCirclePlay} id="play-icon" onClick={() => setIsPlaying(!isPlaying)}/>
            </button>
        </div>
    )
}

export default SongPage
