import React from 'react'
import { faX } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const AboutPop = ({close}) => {
  return (
    <div className='popup'>
        <h1>About</h1>
        <FontAwesomeIcon icon={faX} onClick={close}/>
        <p>Each daily 2010s-Heardle features a clip from a popular 2010s song. Guess in as few tries as possible, and come back every day for a new song!</p>


    </div>
  )
}

export default AboutPop
