import React, { useState,useEffect, useRef } from 'react'
import YouTube from 'react-youtube';

const OPTS = {width: "0", height:"0"}

const Player = ({guessTime, songID,startOffset, isPlaying, setIsPlaying}) => {
  const playerRef = useRef()
  const [timeoutID, setTimeoutID] = useState()
  const [timeStarted, setTimeStarted] = useState(new Date())

  //handles song having been played for allocated time
  const onTimeout = () =>{
    setIsPlaying(false);
    playerRef.current.internalPlayer.pauseVideo();
  }


  useEffect(()=>{
    //forces song to load
    playerRef.current.internalPlayer.playVideo();
    playerRef.current.internalPlayer.pauseVideo();

    playerRef.current.internalPlayer.setVolume(100);
  },[])

  //handles play/pausing of player
  useEffect(()=>{
    if(!playerRef.current) return
    if(isPlaying){
      playerRef.current.internalPlayer.seekTo(startOffset);
      playerRef.current.internalPlayer.playVideo();
      //let guessTime = (1 + guessNum*(guessNum+1)/2) * 1000
      console.log(guessTime)
      setTimeoutID(setTimeout(onTimeout,guessTime*1000)) //guess time secs -> ms
      setTimeStarted(new Date())
    } else{
      playerRef.current.internalPlayer.pauseVideo();
      clearTimeout(timeoutID)
      console.log(`time left: ${timeoutID}`)
    }
  },[isPlaying])

  //handles when the user skips while the song is playing (so keeps playing until new time slot)
  useEffect(()=>{
    if(guessTime == 1) return //when guessNum is first set
    clearTimeout(timeoutID);
    let timeRemaining = guessTime * 1000 - ((new Date()) - timeStarted);
    console.log((new Date()) - timeStarted);
    setTimeoutID(setTimeout(onTimeout,timeRemaining))
  }, [guessTime])

  return (
    <YouTube opts={OPTS} videoId={songID} onReady={()=>console.log("READY")} ref={playerRef}/>
  )
}

export default Player
