import React, { useEffect, useRef, useState } from 'react'
import useGlobal from '../hooks/useGlobal'
import YouTube from 'react-youtube';
import Player from './Player';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

const OPTS = {width: "0", height:"0"}

const SongPage = () => {
    const {CORRECT_SONG} = useGlobal();
    const [duration, setDuration] = useState(0);
    const [isPlaying, setIsPlaying] = useState(true);
    const [timeElapsed,setTimeElapsed] = useState(0)
    const [intervalID, setIntervalID] = useState();

    const imgURL = `https://img.youtube.com/vi/${CORRECT_SONG.id}/maxresdefault.jpg`

    const openInNewTab = url => {
        window.open(url, '_blank', 'noopener,noreferrer');
    };

    /*TODO:
    Also, work out how to re-use songbar logic (number of bars as a parameter [can just use = 1 in songpage, then remove % of the first index so automatically fills rest of space] and then length)
    */
    return (
        <div id="songpage-container">
            <div id="song-box" onClick={() => openInNewTab(`https://youtu.be/${CORRECT_SONG.id}&t=${Math.floor(CORRECT_SONG.offset)}`)}>
                <img src={imgURL} id="thumbnail-img"/>
                <div id="name-and-artist">
                    <p>{CORRECT_SONG.name}</p>
                    <p id='song-artist'>{CORRECT_SONG.artist}</p>
                </div>
                <img src={"https://cdn-icons-png.flaticon.com/512/1384/1384060.png"} id="yt-icon"></img>
            </div>
            <Player guessTime={duration} songID={CORRECT_SONG.id} startOffset={CORRECT_SONG.offset} isPlaying={true} setIsPlaying={setIsPlaying}/>            
            <button className="play-btn">
                <FontAwesomeIcon icon = {faCirclePlay} id="play-icon" onClick={() => setIsPlaying(!isPlaying)}/>
            </button>

        </div>
    )
}

export default SongPage
