import { faCirclePlay, faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useEffect } from 'react'
import { useState, useRef } from 'react';

const NUM_OF_BARS = 6
const Body = () => {

  const [guesses, setGuesses] = useState(new Array(NUM_OF_BARS));
  const [guessNum, setGuessNum] = useState(3)
  const [currentGuess, setCurrentGuess] = useState("")
  const searchRef = useRef();

  useEffect(() =>{
    setGuesses(["","","","","",""])
  }, [])

  //runs every time current guess changes (TODO: Searches for matches)
  useEffect(() =>{
    console.log(currentGuess)
  },[currentGuess])

  return (
    <>
      <div className='bars'>
        {guesses.map((g,i) =>{
          return  <div className={`guess-box ${(guessNum == i) ? "active":""}`} key={i}>{g}</div>
        })}
      </div>

      <div id="progress-bar"/>
      <FontAwesomeIcon icon = {faCirclePlay} id="play-btn" onClick={() => console.log("play")}/>

      <div id="search-songs">
        <label htmlFor="searchbar">
          <FontAwesomeIcon id="search-icon" icon={faMagnifyingGlass} />
        </label>
        <input
        type="text"
        id="searchbar"
        ref={searchRef}
        autoComplete="off"
        onChange={(e) => setCurrentGuess(e.target.value)}
        />
      </div>

      <div id="submit-btns-container">
        <button id="skip">Skip (+1s)</button>
        <button id="submit">SUBMIT</button>
      </div>
    </>
  )
}

export default Body
