import React, {useRef} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faShareNodes } from '@fortawesome/free-solid-svg-icons'


const SHARE_LINK = "www.2010heardle.com";
const ShareButton = () => {
    const textAreaRef = useRef();


    const copyToClipboard = (text) =>{
      const isIos = !!navigator.userAgent.match(/ipad|iphone/i);

      if(isIos){
        const range = document.createRange();
        range.selectNodeContents(textAreaRef.current)
      }

      navigator.clipboard.writeText(text)
    }
    return (
    <>
      <textarea style={{display:"none"}} value={SHARE_LINK} readOnly={true} ref={textAreaRef}></textarea>
      <button id="cp-link" onClick={() => copyToClipboard(SHARE_LINK)}> <FontAwesomeIcon icon={faShareNodes}/>Copy Link</button>
    </>
  )
}

export default ShareButton
