  const [play, setPlay] = React.useState<boolean>(false);

  const [currentTime, setCurrentTime] = React.useState<number>(0);

  const [isReady, setIsReady] = React.useState<boolean>(false);


  useEffect(()=>{
    if(guessNum !== undefined) return;
    const getDuration = (async() =>{
        const durationPromise = playerRef.current.internalPlayer.getDuration()
        durationPromise.then((d)=>{
          setDuration(Math.floor(d - CORRECT_SONG.offset))
        }
    )})();

  },[playerRef])


  React.useEffect(() => {
    setInterval(() => {
      playerRef.current?.internalPlayer
        .getCurrentTime()
        .then((time: number) => {
          setCurrentTime(time);
        });
    }, 250);
  }, []);

  React.useEffect(() => {
    if (play) {
      if (currentTime * 1000 >= currentPlayTime) {
        playerRef.current?.internalPlayer.pauseVideo();
        playerRef.current?.internalPlayer.seekTo(0);
        setPlay(false);
      }
    }
  }, [play, currentTime]);

  // don't call play video each time currentTime changes
  const startPlayback = React.useCallback(() => {
    playerRef.current?.internalPlayer.playVideo();
    setPlay(true);
    event({
      category: "Player",
      action: "Played song",
    });
  }, []);

  const setReady = React.useCallback(() => {
    setIsReady(true);
  }, []);


  return (
    <>
      <ProgressBar isPlaying={isPlaying} guessNum={guessNum} duration={duration}/>
      <button className="play-btn">
        <FontAwesomeIcon icon = {isPlaying? faCircleStop : faCirclePlay} id="play-icon" onClick={onPlayClicked}/>
      </button>
    </>
    )
}

export default Player
