import React, { useEffect } from 'react'
import { useState } from 'react';

const NUM_OF_BARS = 6
const Body = () => {

  const [guesses, setGuesses] = useState(new Array(NUM_OF_BARS));
  const [guessNum, setGuessNum] = useState(0)

  useEffect(() =>{
    setGuesses(["","","","","",""])
  }, [])

  return (
    <>
    <div className='bars'>
      {guesses.map((g,i) =>{
        return  <div className={`guess-box ${(guessNum == i) ? "active":""}`}>{g}</div>
      })}
    </div>

    <div id="progress-bar"/>
    </>
  )
}

export default Body
