import { createContext,useEffect,useRef,useState } from "react";
import raw from '../rsc/songs.txt';

const GlobalContext = createContext({});

export const GlobalProvider = ({children}) =>{

    const [hasPlayedToday, setHasPlayedToday] = useState(false);
    const [isCorrect, setIsCorrect] = useState(false);
    const [isPlaying, setIsPlaying] = useState(false);
    const [duration, setDuration] = useState(0)
    const [correctSong, setCorrectSong] = useState("") 

    const playerRef = useRef(); 


    useEffect(()=>{
        fetch(raw)
        .then(r => r.text())
        .then(text => {
          let s = JSON.parse(text);
          setCorrectSong(s[0])
        });
    })

    return(
        <>
        {correctSong? 
        (<GlobalContext.Provider value={{correctSong,hasPlayedToday,setHasPlayedToday,isCorrect,setIsCorrect,isPlaying,setIsPlaying, playerRef, duration, setDuration}}>
            {children}
        </GlobalContext.Provider>)
        :
        <></>
        }
        </>
    )
}

export default GlobalContext;
