import { faCirclePlay, faMagnifyingGlass, faSquareXmark } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useEffect } from 'react'
import { useState, useRef } from 'react';
import Player from "./Player"
import raw from '../rsc/songs.txt';

const CORRECT_SONG = "Timber - Pitbull & Ke$ha"
const SONG_ID = "CTFtOOh47oo"
const START_OFFSET = 1; 
const NUM_OF_BARS = 6
const Body = () => {

  const [songs, setSongs] = useState([])
  const [guesses, setGuesses] = useState(new Array(NUM_OF_BARS));
  const [guessNum, setGuessNum] = useState(0)
  const [currentGuess, setCurrentGuess] = useState("")
  const [songMatches, setSongMatches] = useState([])
  const [isPlaying, setIsPlaying] = useState(false)
  const [timeElapsed, setTimeElapsed] = useState(0)
  const [intervalID, setIntervalID] = useState()

  const searchRef = useRef();

  useEffect(() =>{
    fetch(raw)
        .then(r => r.text())
        .then(text => {
          let s = JSON.parse(text);
          setSongs(s);
          //setSongMatches(s)
        });
    setGuesses(["","","","","",""])
  }, [])

  //runs every time current guess changes (TODO: Searches for matches)
  useEffect(() =>{
    if(currentGuess == ""){
      return;
    }
    //TODO: make this more efficient with tree / linked list type structure of popping songs on and off it instead of searching through all songs every time
    setSongMatches(songs.filter(obj =>{
      let s = obj.song.toLowerCase();
      let a = obj.artist.toLowerCase(); 
      let g = currentGuess.toLowerCase();
      return `${s} - ${a}`.includes(g);
  }));
  },[currentGuess])

  useEffect(() =>{
    console.log({songMatches})
  }, [songMatches])

  //used to update elapsed time
  useEffect(() =>{
    if(!isPlaying){
      setTimeElapsed(0);
      if(intervalID) clearInterval(intervalID);
      return
    }

    let ms10 = 0;
    setIntervalID(setInterval(()=>setTimeElapsed(++ms10),100))
  },[isPlaying])

  const updateGuessesArray = (x) =>{
    setGuesses(guesses.map((e,i) => {
      return i == guessNum? x : e;
    }))
  }

  const onSkip = () =>{
    updateGuessesArray("SKIPPED")
    setGuessNum(guessNum + 1)
  }

  //this may not correctly work asynchronously
  const onSubmit = () =>{
    updateGuessesArray(currentGuess);
    setGuessNum(guessNum + 1)
  }

  return (
    <>
      <div className='bars'>
        {guesses.map((g,i) =>{
          return  <div className={`guess-box ${(guessNum == i) ? "active":""}`} key={i}>
            <FontAwesomeIcon id= "square-x-icon" icon = {faSquareXmark} className = {guessNum <= i ? "invisible" : (guesses[i] != "SKIPPED"? "incorrect":"")} /> 
            {/*className={currentGuess > i ? "":"invisible"}*/ }
            {g}
            </div>
        })}
      </div>


      <div id = "progress">
        <div id="progress-bar">
          <div id="contents">
            {[...Array(6)].map((x, i) =>
              <div id={`p${i}`} key={i} className= {`partial-bar ${guessNum >= i? "full": ""}`} />
            )}
          </div>
        </div>
        <div id="progress-green" style={{width:`${(timeElapsed /1.6)}%`}}/>
        <div id="timers">
          <p>0:{timeElapsed >=100? Math.floor(timeElapsed/10):`0${Math.floor(timeElapsed/10)}`}</p>
          <p>0:16</p>
        </div>
      </div>

      <button id="play-btn">
        <FontAwesomeIcon icon = {faCirclePlay} id="play-icon" onClick={() => setIsPlaying(!isPlaying)}/>
      </button>


      <div id="guess-container">
        <div id="song-matches">
          {songMatches.slice(0,10).map((e,i) =>
          <div 
          id = {`match${i}`} 
          key={i} 
          className='song-match'
          onClick = {() => setCurrentGuess(`${e.song} - ${e.artist}`)}
          >
          {`${e.song} - ${e.artist}`}
          </div>
          )}
        </div>
        <div id="search-songs">
          <label htmlFor="searchbar">
            <FontAwesomeIcon id="search-icon" icon={faMagnifyingGlass} />
          </label>
          <input
          type="text"
          id="searchbar"
          ref={searchRef}
          autoComplete="off"
          onChange={(e) => setCurrentGuess(e.target.value)}
          value={currentGuess}
          />
        </div>
      </div>
      <div id="submit-btns-container">
        <button id="skip" onClick={onSkip}>Skip (+{guessNum+1}s)</button>
        <button id="submit" onClick = {onSubmit} disabled={searchRef?.current?.value ==""}>Submit</button>
      </div>
      <Player songID={SONG_ID} startOffset={START_OFFSET} guessNum={guessNum} isPlaying ={isPlaying} setIsPlaying={setIsPlaying}/>

    </>
  )
}

export default Body
