import React, { useEffect, useRef, useState } from 'react'
import useGlobal from '../hooks/useGlobal'
import YouTube from 'react-youtube';

const SongPage = () => {
    const playerRef = useRef();
    const {CORRECT_SONG} = useGlobal();

    const [duration, setDuration] = useState();

    useEffect(()=>{

        setDuration((async() =>{
            const durationPromise = await playerRef.current.internalPlayer.getDuration()
            return durationPromise
        })());


        //playerRef.current.internalPlayer.getDuration().then(d => console.log(d));

    },[playerRef])

    useEffect(()=>{
        console.log(duration)
    },[duration])

    return (
    <YouTube videoId={CORRECT_SONG.id} onReady={()=>console.log("READY")} ref={playerRef}/>
  )
}

export default SongPage
